import React, { Component } from "react";
import { Form, Input, Button, Divider } from 'antd';
import axios from 'axios'
import '../App.css'
const { TextArea } = Input;

class Contact extends Component {
  constructor(props) {
    super(props)

    this.state = {
      firstName: '',
      lastName: '',
      email: '',
      txetarea: '',
    }
  }
  postContact = () => {
    axios.post(`http://localhost:3000/contact`,
    )
      .then((response) => {
        console.log("RESPONSE: ", response);
      })
      .catch((err) => {
        console.log("ERR: ", err);
      });
    };
    handleSubmit = (e) => {
      e.preventDefault();
      this.setState({
          [ e.target.name]:e.target.value
        });
    };
  render() {
    const layout = {
      labelCol: { span: 8 },
      wrapperCol: { span: 16 },
    };
    const tailLayout = {
      wrapperCol: { offset: 8, span: 16 },
    };
    const onFinish = (values) => {
      postContact(values);
      console.log('Success:', values);
    };
    const onFinishFailed = (errorInfo) => {
      console.log('Failed:', errorInfo);
    };
    return (
      <div>

        <Divider orientation="center" type="horizontal">
          <h1 className="large-font contact-title">
            <b>Contact Us</b>
          </h1>

        </Divider>
        <div className='contact-container'>
          <Form
            {...layout}
            name="basic"

            onFinish={onFinish}
            onFinishFailed={onFinishFailed}
          >
            <Form.Item
              label="Firstname"
              name="Firstname"
              rules={[{ required: true, message: 'Please input your firstname!' }]}
              value={this.state.firstname} onChange={this.handleSubmit}
            >
              <Input />
            </Form.Item>
            <Form.Item
              label="Lastname"
              name="Lastname"
              rules={[{ required: true, message: 'Please input your Lastname!' }]}
              value={this.state.lastname} onChange={this.handleSubmit}
            >
              <Input />
            </Form.Item>

            <Form.Item
              label="email"
              name="email"
              rules={[{ required: true, message: 'Please input your email!' }]}
              value={this.state.email} onChange={this.handleSubmit}
            >
              <Input />
            </Form.Item>

            <Form.Item {...tailLayout} >
              <TextArea placeholder='Write something..' rows={4} 
              value={this.state.txetarea} onChange={this.handleSubmit}
              />
            </Form.Item>

            <Form.Item {...tailLayout}>
              <Button type="primary" htmlType="submit"
              onClick={this.handleSubmit}
              >
                Send
            </Button>
            </Form.Item>
          </Form>
        </div>
      </div>
    );
  }
}
export default Contact;
